import rsa
import base64

# Chave pública em formato PEM
public_key_pem = """
-----BEGIN PUBLIC KEY-----

-----END PUBLIC KEY-----
"""

# Chave de API
api_key = "dgpmd2cqz34f1qo5pdw37jst7tmrwtcl"

# Carregar a chave pública
public_key = rsa.PublicKey.load_pkcs1_openssl_pem(public_key_pem.encode())

# Criptografar a chave de API com a chave pública
encrypted_api_key = rsa.encrypt(api_key.encode(), public_key)

# Codificar em Base64
encoded_api_key = base64.b64encode(encrypted_api_key).decode()

print(f"Authorization: Bearer {encoded_api_key}")
